const productsWrapper=document.getElementById("productsWrapper")
let CartItem=[]

const cartproducts=document.getElementById("cartProducts")

// To Fetch Data Form API

async function FetchProducts(){
   let response=await fetch("https://fakestoreapi.com/products")
   let allproducts=await response.json()
   DisplayProducts(allproducts) //Sending All Product to DisplayProducts Function
}

FetchProducts()
function DisplayProducts(Products){
   Products.map((Product)=>{

      // Create Element
      let card=document.createElement("section")
      let image=document.createElement("img")
      let product_title=document.createElement("h2")
      let product_Desc=document.createElement("p")
      let product_Price=document.createElement("p")
      let add_to_cart=document.createElement("button")

      // Adding TextContent 
      product_title.textContent=Product.title
      product_Desc.textContent=`${Product.description.slice(0,20)}...`
      product_Price.innerHTML=`&#8377; Price${Product.price}`
      add_to_cart.textContent="Add To Card"

      

      // ! addEventListener
      add_to_cart.addEventListener("click",()=>{
         console.log("item add to card");
         console.log(Product);
         // finding if product exists in cart or not
         let existingProduct=CartItem.find((ele)=>ele.id===Product.id)
         // if it exists increase quantity +1
         if(existingProduct){
            existingProduct.quantity+=1;
         }
         else{
            // push product in the cart with a new key i.e Quantity
            CartItem.push({...Product,quantity:1})
            add_to_cart.textContent="Add More             "
         }
         // localStorage
         localStorage.setItem("cart",JSON.stringify(CartItem))
         console.log(CartItem);
         // Section 2 Code
         DisplayCartItems()
       
         
      })
      // Setting Attribute
      card.setAttribute("class","card")
      image.setAttribute("src",Product.image)

      card.append(image,product_title,product_Desc,product_Price,add_to_cart)
      

      productsWrapper.append(card)

   })
}
// Section 2 Code
function DisplayCartItems(){
   // 
   console.log("DisplayCartItems");
   // Clearing Pervious Html
   cartproducts.innerHTML="";
   // Fetching Cart items form LocalStroage
   let cartData=JSON.parse(localStorage.getItem("cart"))
   console.log(cartData);
// Iterating cart item and Displaying on UI
   cartData.map((item)=>{
      let cartCard=document.createElement("article")
      let itemImage=document.createElement("img")
      let itemTitle=document.createElement("h1")
      let itemQuanity=document.createElement("p")
      let itemPrice=document.createElement("p")
     

      itemImage.setAttribute("src",item.image)
      itemQuanity.textContent=item.title;
      itemQuanity.textContent=item.quantity
      itemPrice.textContent=itemQuanity * item.price;
      cartCard.append(itemImage,itemTitle,itemQuanity,itemPrice)
      cartproducts.append(cartCard)
   
   });
   
}


